  $(function() {
    var character, characters;
    area = create_world_from_height_map(heightMap);
    characters = initialize_characters();
    drawTiles(area);
    character = $('<div id="character" style="position:absolute; left:300px; top:100px; z-index:100;"></div>');
    $("#screen").append(character);
    player = characters[0];
    return shiftScreen();
  });

  $(document).bind("keydown", "w", function() {
    return moveCharacterUp(area, player);
  });

  $(document).bind("keydown", "s", function() {
    return moveCharacterDown(area, player);
  });

  $(document).bind("keydown", "d", function() {
    return moveCharacterRight(area, player);
  });

  $(document).bind("keydown", "a", function() {
    return moveCharacterLeft(area, player);
  });

  moveCharacterUp = function(area, character) {
    var x, y;
    x = character.x + 1;
    y = character.y - 1;
    if (passable(area, character, x, y)) {
      return updateCharacter(area, character, x, y);
    }
  };

  moveCharacterDown = function(area, character) {
    var x, y;
    x = character.x - 1;
    y = character.y + 1;
    if (passable(area, character, x, y)) {
      return updateCharacter(area, character, x, y);
    }
  };

  moveCharacterLeft = function(area, character) {
    var x, y;
    x = character.x - 1;
    y = character.y - 1;
    if (passable(area, character, x, y)) {
      return updateCharacter(area, character, x, y);
    }
  };

  moveCharacterRight = function(area, character) {
    var x, y;
    x = character.x + 1;
    y = character.y + 1;
    if (passable(area, character, x, y)) {
      return updateCharacter(area, character, x, y);
    }
  };

  updateCharacter = function(area, character, x, y) {
    var tile;
    tile = area[x][y];
    character.x = x;
    character.y = y;
    character.z = tile.z;
    if (character === player) return shiftScreen();
  };

  shiftScreen = function() {
    var canvasLeft, canvasTop;
    canvasLeft = -tileSize * 2 * player.x + 300;
    canvasTop = -tileSize * player.y + 150 + player.z * displayHeight;
    $("#canvas").animate({
      "left": canvasLeft,
      "top": canvasTop
    }, "linear");
    return $("#character").animate({
      "z-index": player.y
    }, "linear");
  };

